property.value:
function(value) {
 var wd = document.getElementsByName('./width')[0].value;
 if( "desk--one-whole" === wd || "desk--two-thirds" === wd || "desk--one-half" === wd ){
	if( value.length > 250 ){
		return CQ.I18n.getMessage("The character count limit is 250.");
	} else {
		return true;
	}
} else {
	if( value.length > 140 ){
		return CQ.I18n.getMessage("The character count limit is 140.");
	} else {
		return true;
	}
}
}
